import React, { useState } from "react";
import "./LogIn.css";

function LogIn() {
  const [emailLog, setEmailLog] = useState();
  const [passLog, setPassLog] = useState();

  const HandleLogIn = () => {
    var usersData = [];
    usersData =
      JSON.parse(localStorage.getItem("Data")) == null
        ? []
        : JSON.parse(localStorage.getItem("Data"));
    if (emailLog == "" || passLog == "") {
      alert("Field is monditory");
    } else {
      var Status = false;
      for (let index = 0; index <= usersData.length - 1; index + 1) {
        if (emailLog == value[index].Email && passLog == value[index].Pass) {
          Status = true;
          break;
        }
      }
      //   var nul = usersData.map((value, index) => {
      //     emailLog == value.Email && passLog == value.Pass ? true : false;
      //   });

      if (nul === true) {
        alert("Match");
        console.log("nul", nul);
      }
    }
  };
  return (
    <div className="LogIn">
      <div className="LogIn_Inner">
        <h1 className="Header">Log In</h1>
        <div className="LogIn_Inner_Email">
          <h5>Email</h5>
          <input
            type="text"
            placeholder="Type Your Email"
            onChange={(e) => {
              setEmailLog(e.target.value);
            }}
          />
        </div>
        <div className="LogIn_Inner_Password">
          <h5>Password</h5>
          <input
            type="text"
            placeholder="Type Your Password"
            onChange={(e) => {
              setPassLog(e.target.value);
            }}
          />
          <h4 style={{ margin: "auto", marginTop: "10px" }}>
            Forgot Password?
          </h4>
        </div>
        <div className="Register_Inner_Footer">
          <button type="button" onClick={HandleLogIn}>
            LogIn
          </button>

          <h4>
            Don't have an account? <span>Register.</span>
          </h4>
        </div>
      </div>
    </div>
  );
}

export default LogIn;
