import React, { useState } from "react";
import "./Practice.css";
import { Table, Input } from "reactstrap";
import "bootstrap/dist/css/bootstrap.css";
import TableCom from "./TableCom";
import axios from "axios";
import {
  ButtonDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
} from "reactstrap";

function Practice() {
  const [value, setValue] = useState();
  const [cond, setCond] = useState(false);
  const [ButtonData, setBottonData] = useState("Show Me");
  const [search, setSearch] = useState();
  const [searchData, setSearchData] = useState();
  const [searchCond, setSearchCond] = useState(false);
  const [DropdownOpen, setDropdownOpen] = useState(false);
  const [sortCond, setSortCond] = useState(false);
  const [sortData, setSortData] = useState([]);
  const [ButtonHide, setButtonHide] = useState("buttonHide");
  const [ErrorMsg, setErrorMsg] = useState("....");

  const toggleDropDown = () => {
    setDropdownOpen(!DropdownOpen);
  };

  const HandleDataFetch = () => {
    axios
      .get(`https://jsonplaceholder.typicode.com/users`)
      .then((res) => {
        const Data = res.data;
        setValue(Data);
        setCond(!cond);
        if (!cond) {
          setButtonHide("buttonVisible");
          setBottonData("Hide Me");
        } else {
          setBottonData("Show Me");
          setButtonHide("buttonHide");
        }
      })
      .catch((err) => {
        console.log("err", err.code);
        setErrorMsg(err.code);
      });
  };

  const HandleSearch = (e) => {
    setSearchCond(!searchCond);
    const Search = e.target.value.toLowerCase();
    setSearch(Search);
    const SearchResult = value.filter((value) => {
      if (Search === "") {
        return value;
      } else {
        return value.name.toLowerCase().includes(Search);
      }
    });
    setSearchData(SearchResult);
  };

  const HandleSortData = (e) => {
    setSortCond(!sortCond);
    if (e === "Alpha") {
      const AlphaData = value.sort((a, b) => {
        return a.name.localeCompare(b.name);
      });
      setSortData(AlphaData);
    } else if (e === "Des") {
      const DesData = value.sort((a, b) => {
        return b.id - a.id;
      });
      setSortData(DesData);
    } else {
      setSortData(value);
    }
  };

  return (
    <div className="Wrapper">
      <div className="Wrapper-Header">
        <button type="button" onClick={HandleDataFetch}>
          {ButtonData}
        </button>
        <Input
          type="text"
          placeholder="Search Here"
          onChange={(e) => HandleSearch(e)}
          style={{ width: "50%" }}
        />
        <div>
          <ButtonDropdown
            className={ButtonHide}
            isOpen={DropdownOpen}
            toggle={toggleDropDown}
          >
            <DropdownToggle caret color="primary">
              Sort By
            </DropdownToggle>
            <DropdownMenu>
              <DropdownItem
                onClick={() => {
                  HandleSortData("Alpha");
                }}
              >
                Alphabetical
              </DropdownItem>
              <DropdownItem
                onClick={() => {
                  HandleSortData("Des");
                }}
              >
                Desending
              </DropdownItem>
              <DropdownItem
                onClick={() => {
                  HandleSortData("Nor");
                }}
              >
                Desending
              </DropdownItem>
            </DropdownMenu>
          </ButtonDropdown>
        </div>
      </div>

      <div className="Table">
        <Table>
          <thead>
            <tr>
              <th>User Id</th>
              <th>Name</th>
              <th>Email</th>
              <th>Address</th>
              <th>Contact</th>
            </tr>
          </thead>
          <tbody>
            {(() => {
              if (cond && !searchCond) {
                return <TableCom Value={value} />;
              } else if (searchCond) {
                return <TableCom Value={searchData} />;
              } else if (sortCond && cond) {
                return <TableCom Value={sortData} />;
              } else {
                return (
                  <>
                    {" "}
                    <tr>
                      <td colSpan={5}>Click The Button</td>
                    </tr>
                    <tr>
                      <td colSpan={5} style={{ color: "red" }}>
                        {ErrorMsg}
                      </td>
                    </tr>
                  </>
                );
              }
            })()}
          </tbody>
        </Table>
      </div>
    </div>
  );
}

export default Practice;
